mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT p.prod_no, p.prod_name, p.prod_detail, p.manufacture_Day, p.price, p.image_file, p.reg_date, t.tran_status_code FROM product p, transaction t
t.prod_no(+) = p.prod_no
AND p.prod_no LIKE '%'||#{searchKeyword}||'%'
AND p.prod_name LIKE '%'||#{searchKeyword}||'%'
AND t.tran_status_code IS NULL
AND t.tran_status_code IN ('2 ')
AND t.tran_status_code IN ('3 ')
AND t.tran_status_code IN ('4 ')
AND p.price BETWEEN #{searchKeyword1} AND #{searchKeyword2}
AND p.price BETWEEN #{searchKeyword2} AND #{searchKeyword1}
AND p.price <= #{SearchKeyword1};
ORDER BY p.price
ORDER BY p.price DESC
ORDER BY p.prod_no
ORDER BY p.prod_no
) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
