mapper.select:
SELECT * FROM ( SELECT inner_table.*, ROWNUM AS row_seq FROM ( SELECT t.tran_no, t.prod_no, t.buyer_id, t.payment_option, t.receiver_name, t.receiver_phone, t.demailaddr, t.dlvy_request, t.tran_status_code, t.order_data, t.dlvy_date, t.amount, p.prod_name, p.prod_detail, p.manufacture_day, p.price, p.image_file, p.reg_date AS prodreg, p.quantity, p.stock, u.user_name, u.password, u.role, u.ssn, u.cell_phone, u.addr, u.email, u.reg_date AS ureg FROM transaction t LEFT OUTER JOIN product p ON t.prod_no = p.prod_no LEFT OUTER JOIN users u ON t.buyer_id = u.user_id WHERE t.deleted IS NULL
AND t.buyer_id = #{userId}
AND t.tran_status_code = '2 '
AND t.tran_status_code = '3 '
AND t.tran_status_code = '4 '
ORDER BY t.tran_no ) inner_table WHERE ROWNUM <= #{search.endRowNum} ) WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum}
